import Head from 'next/head'
import Link from 'next/link'
import { useRouter } from 'next/router'
import styles from './Posts.module.css'



function Posts({ posts }) {

    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/post/details')
    }

    return (
    <>
        <Head>
            <title>All Blog Posts</title>
            <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        </Head>
            <Link href="/">
                <a>Go Back</a>
            </Link>
        <ul> {posts.map((post) => ( <li>{post.title}</li> ))} </ul>
        <button onClick={handleClick}>Post Details</button>
    </>
    )
}

export async function getStaticPaths() {
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts`)
    const posts = await res.json()

    let paths = posts.results.map(p => {
        return `/posts/${p.name}`
    })

    return {
        paths,
        fallback: false
    }
}

export async function getStaticProps({ id }) {
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${id.posts}`)
    const posts = await (new Response(blob)).text();

    return {
        props: {
         posts
        }
    }
}


export default Posts
