import Head from 'next/head'
import { useRouter } from 'next/router'

const Post = () => {

    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    
    const router = useRouter();
    const { id, title } = router.query
    

    
    let x = JSON.stringify({id});

    var url = new URL("https://jsonplaceholder.typicode.com/posts/" + id)
    fetch(url)
    .then((response) => response.json())
    .then((json) => console.log(json));

    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: {title}
                    <div>
                        By: {id}
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        
        <script>
            const title = document.getElementById('{ id }');
            const url = `https://jsonplaceholder.typicode.com/posts/{id}`
        </script>
        </>
        )
    };

    export async function getStaticPaths() {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/`)
        const postData = await res.json()
        var paths = [];
        var id = JSON.stringify(paths);
        for(var i in postData)
          paths.push(postData [i]);

        console.log("TESEEESSST:", JSON.stringify(postData))

     
    
        return {
            paths :[
               { params: {postData, id} }
            ], 
                
            fallback: true
        }
    }

    export async function getStaticProps( { params } ) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${params}`)
        const postData = await res.json()
        
        console.log(postData);

        return {
            props: {
                postData
            }
        }
    }
   
    
