import Head from 'next/head'
import { useRouter } from 'next/router'

function Post() {
    
    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }

    const id = router.query
    
    const postData = getPostData(id)
    return (
        <>
            <Head>
                <title>Blog Post: {postData.id} </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post {id} Details:</div>

        <div>
                <div>
                    Title: 
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }
    
    export async function getPostData({ param }) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${param}`)
        const postsArray = await res.json()
        const postData = postsArray.map(data => {
            return data
        })
        console.log(postData)
    
        return {
            postData
        }
    }

export default Post
