import Head from 'next/head'
import React from "react";
import { useRouter } from 'next/router'

const Post = () => {
    
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    const router = useRouter();
    const { id } = router.query;
    var data = getPostData(id);
    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: { id }
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    };

    
   function getPostData({ param }) {
        const res = fetch(`https://jsonplaceholder.typicode.com/posts/${param}`)
        var postData = res.json()
    
        return {
            props:{
                postData
            }
        }
    }


export default Post;
