import Head from 'next/head'
import React from "react";
import { useRouter } from 'next/router'

const Post = ({postData}) => {
    
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }

    const router = useRouter()
    const {id} = router.query
    
    return (
        <>
            <Head>
                <title>Blog Post: {postData} </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post {postData} Details:</div>

        <div>
                <div>
                    Title: 
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    };


   

    export const getStaticPaths = async (id) => {
          console.log("ID")
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
        console.log("RESOURCES: ", res);
        const posts = await res.json()
        
        const paths = posts.map((data) => ({
            params: {
                 id: data.id 
                },
          }))
    
        return {
            paths,
            fallback: false
        };
    };

    export const getStaticProps = async ({params: {postData}}) => {


        return {
            props: {
                postData
            }
        }
    }
export default Post;
