import Head from 'next/head'
import { useRouter } from 'next/router'
// import { Posts } from '../../blog'

export default function Post({ postData }) {
    
    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    
    const id = router.query

    return (
        <>
            <Head>
                <title>Blog Post: {postData.id} </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post {postData.id} Details:</div>

        <div className={styles.container}>
                <div>
                    Title: {postData.title} 
                    <div>
                        By: {postData.userId} 
                    </div>
                </div>
            <div>
                Body: {postData.body} 
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }

    export async function getStaticPaths({ params }) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/`)
        const posts = await res.json()
    
        const paths = posts.map((posts) => ({
            params: {
                 id: posts.id 
                },
          }))
    
        return {
            paths,
            fallback: true
        }
      }
      
      export async function getStaticProps({ params }) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${params}`)
        const posts = await res.json()
    
        return {
            props: {
                posts
            }
        }
      }
