import Head from 'next/head'
import { useRouter } from 'next/router'

function Post() {
    
    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }

    const id = router.query
    
    const postData = getPostData(id)
    return (
        <>
            <Head>
                <title>Blog Post: {postData.id} </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post {id} Details:</div>

        <div>
                <div>
                    Title: 
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }
    
   export function getPostData( id ) {
        const res = fetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
        const postData = {
            id: res.id
        }
    
        return {
            postData
        }
    }

    export async function getStaticPaths({ id }) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/{id}`)
        
        const paths = res.map(data => ({
            props: {
                 id: data.id 
                },
          }))
    
        return {
            paths,
            fallback: true
        }
    }
    
    export async function getStaticProps() {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/`)
        const postData = await res.json()
    
        return {
            props:{
                postData
            }
        }
    }

export default Post
