import Head from 'next/head'
import { useRouter } from 'next/router'

const Post = ({ newData }) => {

    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    
    const router = useRouter();
    const { id, title } = router.query

   // var url = new URL("https://jsonplaceholder.typicode.com/posts/?q=id:" + id)
  // fetch(url)

    
    

  //  const obj =  fetchData({id})
   // console.log("TEST VARIABLES: ", obj )
    
  //  console.log("TEST VARIABLES2: ", newData )

    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: {newData?.title}
                    <div>
                        By User: {id}
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }

    
    async function fetchData({id}){
        let response = await fetch('https://jsonplaceholder.typicode.com/posts/?' + new URLSearchParams({ id: id }));


        var data = await response.json();
        console.log("POST DATA: ", data);

        var newData = [];
        
        for(var i in data)
          newData.push(data [i]);

        var json = JSON.stringify(newData)
          var parData = JSON.parse(json)

        console.log("NEW DATA: ", newData)
        console.log("TITLE: ", json)
        console.log("PARSED: ", parData)

        return  newData;
        
        
    }

    export async function getStaticPaths( id ) {
        const res = await fetch('https://jsonplaceholder.typicode.com/posts/?' + new URLSearchParams({ id: id }))
        const newData = await res.json()

        console.log("TEST:", JSON.stringify(newData))

        let paths = newData.results
     
    
        return {
            paths :[
               { params: {} }
            ],
            fallback: true
        }
    }

    export async function getStaticProps({ id }) {
        const res = await fetch('https://jsonplaceholder.typicode.com/posts/?' + new URLSearchParams({ id: id }))


        const newData = await res.json()
        console.log("POST DATA: ", newData)

      //  var newData = [];
        
        //for(var i in data)
        //  newData.push(data [i]);

      //  var json = JSON.stringify(newData)
        //  var parData = JSON.parse(json)

       // console.log("NEW DATA: ", newData)
       // console.log("TITLE: ", json)
       // console.log("PARSED: ", parData)

        return {
            props: {
                newData
            }
        }
    }
   


export default Post;
