import Head from 'next/head'
import { useRouter } from 'next/router'

const Post = ({ newData }) => {

    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    
    const router = useRouter();
    const { id } = router.query

   // var url = new URL("https://jsonplaceholder.typicode.com/posts/?q=id:" + id)
  // fetch(url)

    
 fetch('https://jsonplaceholder.typicode.com/posts/?' + new URLSearchParams({ id: id }))
  .then(function (response) {
    // The JSON data will arrive here
  })

  const data =  response.json();
  console.log("POST DATA: ", data);

  var newData = [];
  
  for(var i in data)
    newData.push(data [i]);

  var json = JSON.stringify(newData)
    var parData = JSON.parse(json)

  console.log("NEW DATA: ", newData)
  console.log("TITLE: ", json)
  console.log("PARSED: ", parData)
    


    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: {newData?.title}
                    <div>
                        By User: {id}
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }

    
     async function fetchData({id}){
        const response = await fetch('https://jsonplaceholder.typicode.com/posts/?' + new URLSearchParams({ id: id }));


        const data =  response.json();
        console.log("POST DATA: ", data);

        var newData = [];
        
        for(var i in data)
          newData.push(data [i]);

        var json = JSON.stringify(newData)
          var parData = JSON.parse(json)

        console.log("NEW DATA: ", newData)
        console.log("TITLE: ", json)
        console.log("PARSED: ", parData)

        return  newData;
        
        
    }

    export async function getStaticPaths() {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/`)
        const postData = await res.json()
        var paths = [];
        var id = JSON.stringify(paths);
        for(var i in postData)
          paths.push(postData [i]);

        console.log("TEST:", JSON.stringify(postData))

     
    
        return {
            paths :[
               { params: {postData, id} }
            ], 
                
            fallback: true
        }
    }

    export async function getStaticProps( { params } ) {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${params}`)
        const postData = await res.json()
        
        console.log(postData);

        return {
            props: {
                postData
            }
        }
    }
   


export default Post;
