import Head from 'next/head'
import React from "react";
import axios from "axios";
import { useRouter } from 'next/router'
import { render } from 'react-dom';

const Post = () => {

    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }

    

    const router = useRouter();
    const query = router.query;
    var { id } = router.query
    var x = JSON.stringify(query);
    var y = JSON.parse(x);
    console.log("KEYS: ", Object.keys(query));
    console.log("VAULUES: ", Object.keys(query));
    
    console.log("ENTRIES: ", Object.keys(query));


    function getPostData() {
            axios.get(`https://jsonplaceholder.typicode.com/${postID}`) 
            .then((response) => {
                const postData = response.data;
                this.setState({ postData })
            })
            .catch(console.log)     
    }

  //  var data = getPostData();
    var res = '';

    function getPostNum(x, y) {
        for (var i in x) {
            if (x[i] == y){
                return i;
            }
        }
        return false;
    }
    var postID = getPostNum(x, x.route);
    console.log("ROUTE: ", postID);

    

    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: { id }
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    };


    function getPostId() {
        const router = useRouter();
        const { x } = router.query;
        var data = {x}
        console.log(x)
        return {
            data
        }
    }
    
  


export default Post;
