import Head from 'next/head'
import Link from 'next/link'
import path from 'path'
import { useRouter } from 'next/router'



function Posts({ postData }) {

    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/')
    }



    return (
    <>
        <Head>
            <title>All Blog Posts</title>
            <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        </Head>
            <button onClick={handleClick}>Go Back</button>
        <ul> 
                <li>
                    <Link href={`/posts/${encodeURIComponent(postData?.id)}`}>
                        <a>{postData?.title}</a>
                    </Link>
                </li> 
        </ul>
    </>
    )
}


export async function getStaticPaths() {
        const res = await fetch(`https://jsonplaceholder.typicode.com/posts`)
        const postData = await res.json()
        var paths = [];
        var id = JSON.stringify(paths);
        for(var i in postData)
          paths.push(postData);

     
        return {
            paths :[
               { params: {postData} }
            ], 
                
            fallback: true
        }
}

export async function getStaticProps({ param }) {
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${param}`)
    const postData = await res.json()

    return {
        props:{
            postData
        }
    }
}


export default Posts;
