import React from "react";
import { useRouter } from 'next/router'

const Post = () => {
    
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    const router = useRouter();
    const { id } = router.query;
    var postData = getPostId();
    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: { id }
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    };


    function getPostId() {
        const router = useRouter();
        const { x } = router.query;
       var data = new Array(getPostData(x));
        console.log(x)
        return {
            data
        }
    }
    
