import Head from 'next/head'
import Link from 'next/link'
import path from 'path'
import { useRouter } from 'next/router'



function Posts({ posts }) {

    const router = useRouter()
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/')
    }


    return (
    <>
        <Head>
            <title>All Blog Posts</title>
            <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        </Head>
            <button onClick={handleClick}>Go Back</button>
        <ul> 
            {posts.map((postdeet) => (
                <li>
                    <Link href={`/posts/${encodeURIComponent(postdeet.id)}`}>
                        <a>{postdeet.title}</a>
                    </Link>
                </li> 
             ))}
        </ul>
    </>
    )
}


export async function getStaticPaths() {
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts`)
    const posts = await res.json()
    
    const paths = posts.map((data) => ({
        params: {
             id: data.id 
            },
      }))

    return {
        paths,
        fallback: true
    }
}

export async function getStaticProps({param}) {
    const res = await fetch(`https://jsonplaceholder.typicode.com/posts/${param}`)
    const postsArray = await res.json()
    console.log(postsArray)
    const postData = postsArray.map((data) => {
        return data
    })
    console.log(postData)

    return {
        postData
    }
}



export default Posts
