import Head from 'next/head'
import React from "react";
import axios from "axios";
import { useRouter } from 'next/router'
import { render } from 'react-dom';

const Post = () => {
    const router = useRouter();
    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }

    var { id } = router.query
    

    function getPostData() {
        const router = useRouter();
        const query = router.query;
        var x = JSON.stringify(query);
        var y = JSON.parse(x);
        const id = y[1];

        console.log("DATA ID: ", id);
        console.log("KEYS: ", Object.keys(x));

            axios.get(`https://jsonplaceholder.typicode.com/${id}`) 
            .then((response) => {
                const postData = response.data;
                this.setState({ postData })
            })
            .catch(console.log)
            
    }

    var data = getPostData();
    var res = '';

    function getPostNum(x, y) {
        for (var i in x) {
            if (typeof x[i] == 'object') {
                if (propName(x[i], y)) {
                    return res;
                }
            }
            else {
                if (x[i] == y) {
                    res = i;
                    return res;
                }
            }
        }
        return undefined;
    }
    var postID = getPostNum()

    

    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Blog Post { id } Details:</div>

        <div>
                <div>
                    Title: { id }
                    <div>
                        By:
                    </div>
                </div>
            <div>
                Body:
            </div>

        </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    };


    function getPostId() {
        const router = useRouter();
        const { x } = router.query;
        var data = {x}
        console.log(x)
        return {
            data
        }
    }
    
  


export default Post;
