import Head from 'next/head'
import { useRouter } from 'next/router'

const Post = ({ postData }) => {

    const handleClick = (e) => {
        e.preventDefault()
        router.push('/blog/')
    }
    
    const router = useRouter();
    const  { userId }  = router.query
    const  { id }  = router.query
    var x = JSON.stringify(id)
    
    var userIds = JSON.stringify(userId)
    console.log("AUTHOR ID: ", userIds)

    var y = JSON.stringify(postData)
    console.log("TEST: ", y)

    return (
        <>
            <Head>
                <title>Blog Post: { id } </title>
                <meta name="viewport" content="initial-scale=1.0, width=device-width"/>
            </Head>
            <button onClick={handleClick}>Go Back</button>
            <div>Author { id } Details:</div>
            
        <ul>    
            {postData.map((author) => (
                    <li key={author.userId}>
                          1. {author.title}
                          2. {author.body}
                    </li> 
                ))}
        </ul>

        <div id="postContainer">
          
          <div id="title">
            {postData.map((post) => (
                <div>Title: {post.title}</div>
            ))}
          </div>
          <div id="author">
            {postData.map((post) => (
                <div>Author:
                  <Link href={`/blog/post/author/${encodeURIComponent(post?.userId)}`}>
                    <a>{post.userId}</a> 
                  </Link>
                </div>
            ))}
          </div>
          <div id="body">
              {postData.map((post) => (
                  <div>Body: {post.body} </div>
              ))}
          </div>
    </div>

        <button onClick={handleClick}>AuthorDetails</button>
        </>
        )
    }


  
 
    export async function getStaticPaths() {
      const res = await fetch(`https://jsonplaceholder.typicode.com/posts/`)
      const postData = await res.json()
      var paths = [];
      var userId = JSON.stringify(paths)
      for (var i in postData)
        paths.push(postData[i]);
    
      return {
        paths: [
          { params: { postData, userId } }
        ],
    
        fallback: true
      }
    }
    
    export async function getStaticProps( params ) {
      console.log("PARAM: ", params)
      const res = await fetch('https://jsonplaceholder.typicode.com/posts/?=' + params)
      console.log("RESOURCE: ", res)
    
      const postData = await res.json()
      console.log(postData)
    
      if (!postData) {
        return {
          notFound: true,
        }
      }
    
      return {
        props: {
          postData
        }
      }
    }


export default Post;
